    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;typeparam name=&quot;T&quot;&gt;</span>Type of array element.<span class="hljs-doctag">&lt;/typeparam&gt;</span></span>
    <span class="hljs-keyword">public</span> <span class="hljs-keyword">sealed</span> <span class="hljs-keyword">class</span> <span class="hljs-title">MedianOfThreeQuickSorter</span>&lt;<span class="hljs-title">T</span>&gt; : <span class="hljs-title">QuickSorter</span>&lt;<span class="hljs-title">T</span>&gt;
