        <span class="hljs-function"><span class="hljs-keyword">protected</span> <span class="hljs-keyword">override</span> T <span class="hljs-title">SelectPivot</span>(<span class="hljs-params">T[] array, IComparer&lt;T&gt; comparer, <span class="hljs-built_in">int</span> left, <span class="hljs-built_in">int</span> right</span>)</span>
