        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> T <span class="hljs-title">FindMedian</span>(<span class="hljs-params">IComparer&lt;T&gt; comparer, T a, T b, T c</span>)</span>
        {
            <span class="hljs-keyword">if</span> (comparer.Compare(a, b) &lt;= <span class="hljs-number">0</span>)
            {
                <span class="hljs-comment">// a &lt;= b &lt;= c</span>
                <span class="hljs-keyword">if</span> (comparer.Compare(b, c) &lt;= <span class="hljs-number">0</span>)
