﻿<span class="hljs-keyword">using</span> System.Collections.Generic;

<span class="hljs-keyword">namespace</span> <span class="hljs-title">Algorithms.Sorters.Comparison</span>
{
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     Cycle sort is an in-place, unstable sorting algorithm,</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     a comparison sort that is theoretically optimal in terms of the total</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     number of writes to the original array.</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     It is based on the idea that the permutation to be sorted can be factored</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     into cycles, which can individually be rotated to give a sorted result.</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;typeparam name=&quot;T&quot;&gt;</span>Type array input.<span class="hljs-doctag">&lt;/typeparam&gt;</span></span>
    <span class="hljs-keyword">public</span> <span class="hljs-keyword">class</span> <span class="hljs-title">CycleSorter</span>&lt;<span class="hljs-title">T</span>&gt; : <span class="hljs-title">IComparisonSorter</span>&lt;<span class="hljs-title">T</span>&gt;
    {
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     Sorts input array using Cycle sort.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;array&quot;&gt;</span>Input array.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;comparer&quot;&gt;</span>Integer comparer.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">public</span> <span class="hljs-keyword">void</span> <span class="hljs-title">Sort</span>(<span class="hljs-params">T[] array, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = <span class="hljs-number">0</span>; i &lt; array.Length - <span class="hljs-number">1</span>; i++)
            {
                MoveCycle(array, i, comparer);
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-keyword">void</span> <span class="hljs-title">MoveCycle</span>(<span class="hljs-params">T[] array, <span class="hljs-built_in">int</span> startingIndex, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">var</span> item = array[startingIndex];
            <span class="hljs-keyword">var</span> pos = startingIndex + CountSmallerElements(array, startingIndex + <span class="hljs-number">1</span>, item, comparer);

            <span class="hljs-keyword">if</span> (pos == startingIndex)
            {
                <span class="hljs-keyword">return</span>;
            }

            pos = SkipSameElements(array, pos, item, comparer);

            <span class="hljs-keyword">var</span> temp = array[pos];
            array[pos] = item;
            item = temp;

            <span class="hljs-keyword">while</span> (pos != startingIndex)
            {
                pos = startingIndex + CountSmallerElements(array, startingIndex + <span class="hljs-number">1</span>, item, comparer);
                pos = SkipSameElements(array, pos, item, comparer);

                temp = array[pos];
                array[pos] = item;
                item = temp;
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-built_in">int</span> <span class="hljs-title">SkipSameElements</span>(<span class="hljs-params">T[] array, <span class="hljs-built_in">int</span> nextIndex, T item, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">while</span> (comparer.Compare(array[nextIndex], item) == <span class="hljs-number">0</span>)
            {
                nextIndex++;
            }

            <span class="hljs-keyword">return</span> nextIndex;
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-built_in">int</span> <span class="hljs-title">CountSmallerElements</span>(<span class="hljs-params">T[] array, <span class="hljs-built_in">int</span> startingIndex, T element, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">var</span> smallerElements = <span class="hljs-number">0</span>;
            <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = startingIndex; i &lt; array.Length; i++)
            {
                <span class="hljs-keyword">if</span> (comparer.Compare(array[i], element) &lt; <span class="hljs-number">0</span>)
                {
                    smallerElements++;
                }
            }

            <span class="hljs-keyword">return</span> smallerElements;
        }
    }
}
