        <span class="hljs-function"><span class="hljs-keyword">public</span> <span class="hljs-built_in">int</span> <span class="hljs-title">FindIndex</span>(<span class="hljs-params">Span&lt;<span class="hljs-built_in">int</span>&gt; array, <span class="hljs-built_in">int</span> item</span>)</span>
