        <span class="hljs-function"><span class="hljs-keyword">public</span> <span class="hljs-built_in">string</span> <span class="hljs-title">Decode</span>(<span class="hljs-params"><span class="hljs-built_in">string</span> text, <span class="hljs-built_in">double</span>[,] key</span>)</span>
