        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">static</span> <span class="hljs-built_in">char</span>[] <span class="hljs-title">SplitToCharArray</span>(<span class="hljs-params"><span class="hljs-built_in">string</span>[] chunked</span>)</span>
