        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     Having a new node in the heap, swaps this node with its ancestors to make heap valid.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     For node at min level. If new node is less than its parent, then it is surely less then</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     all other nodes on max levels on path to the root of the heap. So node are pushed up, by</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     swaping with its grandparent, until they are ordered correctly.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     For node at max level algorithm is analogical.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;index&quot;&gt;</span>Index of the new node.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">PushUp</span>(<span class="hljs-params"><span class="hljs-built_in">int</span> index</span>)</span>
        {
            <span class="hljs-keyword">if</span> (index == <span class="hljs-number">0</span>)
            {
                <span class="hljs-keyword">return</span>;
            }

            <span class="hljs-keyword">var</span> parent = Parent(index);

            <span class="hljs-keyword">if</span> (IsMinLevelIndex(index))
            {
                <span class="hljs-keyword">if</span> (Comparer.Compare(heap[index], heap[parent]) &gt; <span class="hljs-number">0</span>)
                {
                    SwapNodes(index, parent);
                    PushUpMax(parent);
                }
                <span class="hljs-keyword">else</span>
                {
                    PushUpMin(index);
                }
            }
            <span class="hljs-keyword">else</span>
            {
                <span class="hljs-keyword">if</span> (Comparer.Compare(heap[index], heap[parent]) &lt; <span class="hljs-number">0</span>)
                {
                    SwapNodes(index, parent);
                    PushUpMin(parent);
                }
                <span class="hljs-keyword">else</span>
                {
                    PushUpMax(index);
                }
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">PushUpMax</span>(<span class="hljs-params"><span class="hljs-built_in">int</span> index</span>)</span>
        {
            <span class="hljs-keyword">if</span> (index &gt; <span class="hljs-number">2</span>)
            {
                <span class="hljs-keyword">var</span> grandparent = Grandparent(index);
                <span class="hljs-keyword">if</span> (Comparer.Compare(heap[index], heap[grandparent]) &gt; <span class="hljs-number">0</span>)
                {
                    SwapNodes(index, grandparent);
                    PushUpMax(grandparent);
                }
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">PushUpMin</span>(<span class="hljs-params"><span class="hljs-built_in">int</span> index</span>)</span>
        {
            <span class="hljs-keyword">if</span> (index &gt; <span class="hljs-number">2</span>)
            {
                <span class="hljs-keyword">var</span> grandparent = Grandparent(index);
                <span class="hljs-keyword">if</span> (Comparer.Compare(heap[index], heap[grandparent]) &lt; <span class="hljs-number">0</span>)
                {
                    SwapNodes(index, grandparent);
                    PushUpMin(grandparent);
                }
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">RemoveNode</span>(<span class="hljs-params"><span class="hljs-built_in">int</span> index</span>)</span>
        {
            SwapNodes(index, Count - <span class="hljs-number">1</span>);
            heap.RemoveAt(Count - <span class="hljs-number">1</span>);
            <span class="hljs-keyword">if</span> (Count != <span class="hljs-number">0</span>)
            {
                PushDown(index);
            }
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">SwapNodes</span>(<span class="hljs-params"><span class="hljs-built_in">int</span> i, <span class="hljs-built_in">int</span> j</span>)</span>
        {
            <span class="hljs-keyword">var</span> temp = heap[i];
            heap[i] = heap[j];
            heap[j] = temp;
        }
    }
}
