﻿<span class="hljs-keyword">using</span> System.Collections.Generic;

<span class="hljs-keyword">namespace</span> <span class="hljs-title">Algorithms.Sorters.Comparison</span>
{
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span>     Class that implements pancake sort algorithm.</span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
    <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;typeparam name=&quot;T&quot;&gt;</span>Type of array element.<span class="hljs-doctag">&lt;/typeparam&gt;</span></span>
    <span class="hljs-keyword">public</span> <span class="hljs-keyword">class</span> <span class="hljs-title">PancakeSorter</span>&lt;<span class="hljs-title">T</span>&gt; : <span class="hljs-title">IComparisonSorter</span>&lt;<span class="hljs-title">T</span>&gt;
    {
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     Sorts array using specified comparer,</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     internal, in-place, stable,</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     time complexity: O(n^2),</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     space complexity: O(1),</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span>     where n - array length.</span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;/summary&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;array&quot;&gt;</span>Array to sort.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-comment"><span class="hljs-doctag">///</span> <span class="hljs-doctag">&lt;param name=&quot;comparer&quot;&gt;</span>Compares elements.<span class="hljs-doctag">&lt;/param&gt;</span></span>
        <span class="hljs-function"><span class="hljs-keyword">public</span> <span class="hljs-keyword">void</span> <span class="hljs-title">Sort</span>(<span class="hljs-params">T[] array, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">var</span> n = array.Length;

            <span class="hljs-comment">// Start from the complete array and one by one</span>
            <span class="hljs-comment">// reduce current size by one</span>
            <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> currSize = n; currSize &gt; <span class="hljs-number">1</span>; --currSize)
            {
                <span class="hljs-comment">// Find index of the maximum element in</span>
                <span class="hljs-comment">// array[0..curr_size-1]</span>
                <span class="hljs-keyword">var</span> mi = FindMax(array, currSize, comparer);

                <span class="hljs-comment">// Move the maximum element to end of current array</span>
                <span class="hljs-comment">// if it&#x27;s not already at  the end</span>
                <span class="hljs-keyword">if</span> (mi != currSize - <span class="hljs-number">1</span>)
                {
                    <span class="hljs-comment">// To move to the end, first move maximum</span>
                    <span class="hljs-comment">// number to beginning</span>
                    Flip(array, mi);

                    <span class="hljs-comment">// Now move the maximum number to end by</span>
                    <span class="hljs-comment">// reversing current array</span>
                    Flip(array, currSize - <span class="hljs-number">1</span>);
                }
            }
        }

        <span class="hljs-comment">// Reverses array[0..i]</span>
        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-keyword">void</span> <span class="hljs-title">Flip</span>(<span class="hljs-params">T[] array, <span class="hljs-built_in">int</span> i</span>)</span>
        {
            T temp;
            <span class="hljs-keyword">var</span> start = <span class="hljs-number">0</span>;
            <span class="hljs-keyword">while</span> (start &lt; i)
            {
                temp = array[start];
                array[start] = array[i];
                array[i] = temp;
                start++;
                i--;
            }
        }

        <span class="hljs-comment">// Returns index of the maximum element</span>
        <span class="hljs-comment">// in array[0..n-1]</span>
        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-built_in">int</span> <span class="hljs-title">FindMax</span>(<span class="hljs-params">T[] array, <span class="hljs-built_in">int</span> n, IComparer&lt;T&gt; comparer</span>)</span>
        {
            <span class="hljs-keyword">var</span> mi = <span class="hljs-number">0</span>;
            <span class="hljs-keyword">for</span> (<span class="hljs-keyword">var</span> i = <span class="hljs-number">0</span>; i &lt; n; i++)
            {
                <span class="hljs-keyword">if</span> (comparer.Compare(array[i], array[mi]) == <span class="hljs-number">1</span>)
                {
                    mi = i;
                }
            }

            <span class="hljs-keyword">return</span> mi;
        }
    }
}
